import * as React from 'react'
import shortid from 'shortid'

import {TodoCreateIndividualItemInterface, TodoIndividualItemInterface} from './../interfaces'

const TodoIndividualItem = (props: TodoCreateIndividualItemInterface) => {

    const [timeState, setTime] = React.useState(Date)
    const [formState, setFormState] = React.useState("")
    const inputRef = React.useRef<HTMLInputElement>(null)

    function handleInputChange(event: React.ChangeEvent<HTMLInputElement>){
        setFormState(event.target.value)
    }

    function handleInputEnter(event: React.KeyboardEvent) {
        if(event.key === "Enter") {
            const newTodoItem: TodoIndividualItemInterface = {
                id: shortid.generate(),
                task: formState,
                isCompleted: false
            }

            props.handleTodoCreate(newTodoItem)

            console.log(inputRef, inputRef.current)

            if(inputRef && inputRef.current) {
                inputRef.current.value = ""
            }
        }
    }

    function handleData() {
        let task: any= document.getElementById("taskId")?.textContent;
        let time: any = document.getElementById("dataId")?.textContent;

        if(task != null && time != null) {
            const newTodoItem: TodoIndividualItemInterface = {
                id: shortid.generate(),
                task: formState,
                isCompleted: false,
                setTime: time
            }
            props.handleTodoCreate(newTodoItem)
        }
    }

    return(

        <div className="input-form">
            <input
                id="taskId"
                ref={inputRef}
                type="text"
                placeholder="Enter task"
                onChange={event => handleInputChange(event)}
                onKeyPress={event => handleInputEnter(event)}
                required
            />
            <input
                id="dataId"
                type="date"
                placeholder="Enter date to do the task"
                required
            />
            <button onClick={handleData}>Add task</button>
        </div>

    )
}

export default TodoIndividualItem
