import * as React from 'react'

const CreateButton = (props: any) => {

    let [showFormState, updateFormState] = React.useState<boolean>(false)

    function showForm(){
        return(
            <form>
                <input
                    placeholder="List Title"
                    type="text"
                />
            </form>
        )
    }

    return(
        <div>
            <button className="create-list-btn" onClick={() => showFormState=true}>Create List</button>
            {updateFormState ? showForm(): null}
        </div>
    )
}

export default CreateButton
