import * as React from 'react'
import { render } from '@testing-library/react'

const CreateButton = (props: any) => {

    // let [showFormState, updateFormState] = React.useState()

    function showForm(){
        console.log("show form is called!")
        console.log("called!")
        let formEle = document.getElementById("new-list-form")
        formEle ? formEle.style.display="block" : console.log("none")
    }

    return(
        <div>
            <button className="create-list-btn" onClick={() => {
                    console.log("called!")
                    let formEle = document.getElementById("new-list-form")
                    formEle ? formEle.style.display="block" : console.log("none")
                }

            }>Create a new List</button>
            <form id="new-list-form">
                <input
                    placeholder="List Title"
                    type="text"
                />
            </form>

        </div>
    )
}
export default CreateButton
