import * as React from 'react'

const CreateButton = (props: any) => {

    let [showFormState, updateFormState] = React.useState<boolean>(false)

    function showForm(){
        console.log("show form is called!")
        return(
            <form>
                <input
                    placeholder="List Title"
                    type="text"
                />
            </form>
        )
    }

    return(
        <div>
            <button className="create-list-btn" onClick={() => {showFormState=true 
               {showFormState ? showForm(): null} }}>Create List</button>

        </div>
    )
}

export default CreateButton
